import React from 'react';

const Audio = () => {
    const [matchingResults, setMatchingResults] = React.useState([]);
    const [noMatch, setNoMatch] = React.useState(false);
    const [isLoading, setIsLoading] = React.useState(false);

    const imageList = [1, 2];

    const handleClick = (i) => {
        setIsLoading(true);
        setMatchingResults([]);
        setNoMatch(false);
        const baseUrl = 'http://127.0.0.1:5000/analyze-audio';
        const url = `${baseUrl}/${i}`;
        fetch(url)
            .then(res => res.json())
            .then(res => {
                if (res.success) {
                    setMatchingResults(res.data[0]);
                } else {
                    setNoMatch(true);
                }
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        gap: '20px', // Added gap between the dashboard and buttons
        marginTop: '10px', // Added marginTop for space above the buttons
    };

    const buttonsContainerStyle = {
        display: 'flex',
        gap: '10px',
        marginTop: '1px', // Added marginTop for space above the buttons
    };

    const buttonStyle = {
        backgroundColor: '#E1F4F3',
        padding: '15px',
        width: '100px',
        height: '50px',
        borderRadius: '5px',
        cursor: 'pointer',
    };

    const firstButtonStyle = {
        ...buttonStyle,
        marginLeft: '30px', // Adjusted marginLeft for more space
    };

    return (
        <div style={containerStyle}>
            <div className='dashboard'>
                {/* Your dashboard content here */}
            </div>
            <div className='flex gap-5' style={buttonsContainerStyle}>
                {imageList?.map((item, index) => (
                    <div className='p-10 background-red' key={item} onClick={() => handleClick(item)} style={index === 0 ? firstButtonStyle : buttonStyle}>
                        {item}
                    </div>
                ))}
            </div>
            <div className='flex justify-center items-center'>
                {isLoading ? (
                    <div>Loading....</div>
                ) : (
                    <>
                        {matchingResults && (
                            <div>
                                {matchingResults.folder_name}
                                {matchingResults.matching_percentage}
                            </div>
                        )}
                        {noMatch && <div>No match</div>}
                    </>
                )}
            </div>
        </div>
    );
};

export default Audio;
