import React from 'react';

const Images = () => {
    const baseUrl = 'http://127.0.0.1:5000/analyze-mosquitoes';
    const [imageNumber, setImageNumber] = React.useState(null);
    const imageList = [1, 2, 3, 4, 5, 6];
    const textList = [
        "Text for Sample 1",
        "Text for Sample 2",
        "Text for Sample 3",
        "Text for Sample 4",
        "Text for Sample 5",
        "Text for Sample 6"
    ];

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        gap: '20px',
        position: 'relative', // Added relative position to the container
    };

    const buttonsContainerStyle = {
        display: 'flex',
        gap: '10px',
    };

    const buttonStyle = {
        backgroundColor: '#E1F4F3',
        padding: '10px',
        borderRadius: '5px',
        cursor: 'pointer',
        position: 'relative', // Added relative position to the button
    };

    const textContainerStyle = {
        position: 'absolute', // Position the text container absolutely
        bottom: '-30px', // Adjust the bottom position to align with the button
        left: '10px', // Align the text container to the left
        width: '100px', // Adjust the width as needed
        textAlign: 'left', // Align text to the left
        fontSize: '14px', // Adjust font size
    };

    const headingStyle = {
        fontFamily: 'Arial',
        fontSize: '20px',
        marginBottom: '10px',
        textDecoration: 'underline',
        fontWeight: 'bold',
    };

    const spacerStyle = {
        width: '20px', // Adjust the width for the desired space
    };

    const handleClick = (sampleNumber) => {
        setImageNumber(sampleNumber);
    };

    return (
        <div style={containerStyle}>
            <div className='dashboard'>
                {/* Your dashboard content here */}
            </div>
            <div className='flex' style={buttonsContainerStyle}>
                <div style={spacerStyle}></div> {/* Empty div for horizontal space */}
                <div style={headingStyle}>Image Count Detection</div>
            </div>
            <div className='flex' style={buttonsContainerStyle}>
                {imageList?.map((item, index) => (
                    <div key={item} style={{ position: 'relative' }}>
                        <div style={buttonStyle} onClick={() => handleClick(item)}>
                            Sample {item}
                        </div>
                        <div style={textContainerStyle}>
                            {textList[index]}
                        </div>
                    </div>
                ))}
            </div>
            <div className='flex justify-center items-center'>
                {imageNumber && <img src={`${baseUrl}/${imageNumber}`} width={500} height={250} />}
            </div>
        </div>
    );
};

export default Images;
