import React from 'react'

const Audio = () => {
    
    const [matchingResults, setMatchingResults] = React.useState([])
    const [isLoading, setIsLoading] = React.useState(false)

    const imageList = [1, 2];

    const handleClick = (i) => {
        setIsLoading(true)
        const baseUrl = 'http://127.0.0.1:5000/analyze-audio'
        console.log(i)
        const url = `${baseUrl}/${i}`
        fetch(url)
        .then(res => {
            console.log
            setMatchingResults(res.data)
        })
        .finally(() => {
            setIsLoading(false)
        })
    }

    if(isLoading)
        return <div>Loading....</div>

    return (
       <>
        <div className='flex gap-5'>
            {imageList?.map((item) => (
                <>
                <div className='p-10 background-red' key={item} 
                    onClick={() => handleClick(item)}
                >{item}</div>
                </>
            ))}
        </div>
       <div className='flex justify-center items-center'>
       {/* {audioNumber && <img src={`${baseUrl}/${audioNumber}`} width={500} height={300}/>} */}
       {matchingResults.length > 0 ? (
            <div>
              <h2>Matching Results:</h2>
              <ul>
                {matchingResults.map((result, index) => (
                  <li key={index}>
                    <p>{result}</p>
                  </li>
                ))}
              </ul>
            </div>
          ) : (
            <p>No matches found.</p>
          )}
       </div>
        </>
    )
}

export default Audio
