import React from 'react';

const Audio = () => {
    const [matchingResults, setMatchingResults] = React.useState([]);
    const [noMatch, setNoMatch] = React.useState(false);
    const [isLoading, setIsLoading] = React.useState(false);

    const sampleList = [1, 2, 3, 4, 5]; // Renamed imageList to sampleList

    const handleClick = (sampleNumber) => { // Ensure the parameter is named descriptively
        setIsLoading(true);
        setMatchingResults([]);
        setNoMatch(false);
        const baseUrl = 'http://127.0.0.1:5000/analyze-audio';
        const url = `${baseUrl}/${sampleNumber}`; // Ensure the sampleNumber is directly used in the URL
        fetch(url)
            .then(res => res.json())
            .then(res => {
                if (res.success) {
                    setMatchingResults(res.data[0]);
                } else {
                    setNoMatch(true);
                }
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    const loadingAnimationStyle = {
        borderTop: '2px solid #3498db',
        borderRadius: '50%',
        width: '20px',
        height: '20px',
        animation: 'spin 1s linear infinite', // Apply animation
    };

    if (isLoading)
        return (
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <div style={loadingAnimationStyle}></div>
            </div>
        );

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        gap: '20px',
        alignItems: 'flex-start', // Align items to the left
        justifyContent: 'flex-start', // Align items to the left
    };

    const buttonsContainerStyle = {
        display: 'flex',
        gap: '10px',
    };

    const buttonStyle = {
        backgroundColor: '#E1F4F3',
        padding: '10px',
        borderRadius: '5px',
        cursor: 'pointer',
    };

    const spacerStyle = {
        width: '20px', // Adjust the width for the desired space
    };

    return (
        <div style={containerStyle}>
            <div>
                {/* Your dashboard content here */}
            </div>
            <div style={buttonsContainerStyle}>
                <div style={spacerStyle}></div> {/* Add horizontal space */}
                {sampleList?.map((sample) => (
                    <div style={buttonStyle} key={sample} onClick={() => handleClick(sample)}>
                        Sample {sample}
                    </div>
                ))}
            </div>
            <div className='flex justify-center items-center'>
                {/* Render matching results or no match here */}
            </div>
        </div>
    );
};

export default Audio;
