import React, { useState } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import Sidebar, { SidebarItem } from './components/Sidebar';
import {
  LifeBuoy,
  NotebookPen,
  MapPinned,
  LayoutDashboard,
  Settings,
} from "lucide-react";

const Layout = ({ children }) => {
  const location = useLocation();
  const [activeText, setActiveText] = useState("");

  // Function to set the active text based on the current pathname
  const setActiveTextFromPath = (path) => {
    switch (path) {
      case "/":
        setActiveText("Dashboard");
        break;
      case "/map":
        setActiveText("City Map");
        break;
      case "/readings":
        setActiveText("Readings");
        break;
      case "/settings":
        setActiveText("Settings");
        break;
      case "/help":
        setActiveText("Help");
        break;
      default:
        setActiveText("");
    }
  };

  // Set initial active text when component mounts
  React.useEffect(() => {
    setActiveTextFromPath(location.pathname);
  }, [location.pathname]);

  return (
    <div className="flex">
      <Sidebar>
        <NavLink to="/" activeClassName="active" onClick={() => setActiveText("Dashboard")}>
          <SidebarItem icon={<LayoutDashboard size={20} />} text={activeText === "Dashboard" ? "Dashboard" : "Dashboard"} />
        </NavLink>
        <NavLink to="/map" activeClassName="active" onClick={() => setActiveText("City Map")}>
          <SidebarItem icon={<MapPinned size={20} />} text={activeText === "City Map" ? "City Map" : "City Map"} />
        </NavLink>
        <NavLink to="/readings" activeClassName="active" onClick={() => setActiveText("Readings")}>
          <SidebarItem icon={<NotebookPen size={20} />} text={activeText === "Readings" ? "Readings" : "Readings"} />
        </NavLink>
        <hr className="my-3" />
        <NavLink to="/settings" activeClassName="active" onClick={() => setActiveText("Settings")}>
          <SidebarItem icon={<Settings size={20} />} text={activeText === "Settings" ? "Settings" : "Settings"} />
        </NavLink>
        <NavLink to="/help" activeClassName="active" onClick={() => setActiveText("Help")}>
          <SidebarItem icon={<LifeBuoy size={20} />} text={activeText === "Help" ? "Help" : "Help"} />
        </NavLink>
      </Sidebar>
      <div className="content w-full">{children}</div>
    </div>
  );
};

export default Layout;
