import React, { useState, useEffect } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import Sidebar, { SidebarItem } from './components/Sidebar';
import {
  LifeBuoy,
  NotebookPen,
  MapPinned,
  LayoutDashboard,
  Settings,
} from "lucide-react";

const Layout = ({ children }) => {
  const location = useLocation();
  const [activeText, setActiveText] = useState("");

  // Function to set the active text based on the current pathname
  const setActiveTextFromPath = (path) => {
    switch (path) {
      case "/":
        return "Dashboard";
      case "/map":
        return "City Map";
      case "/readings":
        return "Readings";
      case "/settings":
        return "Settings";
      case "/help":
        return "Help";
      default:
        return "";
    }
  };

  // Set active text when component mounts and when pathname changes
  useEffect(() => {
    setActiveText(setActiveTextFromPath(location.pathname));
  }, [location.pathname]);

  return (
    <div className="flex">
      <Sidebar>
        <NavLink to="/" activeClassName="active" onClick={() => setActiveText("Dashboard")}>
          <SidebarItem icon={<LayoutDashboard size={20} />} text="Dashboard" isActive={activeText === "Dashboard"} />
        </NavLink>
        <NavLink to="/map" activeClassName="active" onClick={() => setActiveText("City Map")}>
          <SidebarItem icon={<MapPinned size={20} />} text="City Map" isActive={activeText === "City Map"} />
        </NavLink>
        <NavLink to="/readings" activeClassName="active" onClick={() => setActiveText("Readings")}>
          <SidebarItem icon={<NotebookPen size={20} />} text="Readings" isActive={activeText === "Readings"} />
        </NavLink>
        <hr className="my-3" />
        <NavLink to="/settings" activeClassName="active" onClick={() => setActiveText("Settings")}>
          <SidebarItem icon={<Settings size={20} />} text="Settings" isActive={activeText === "Settings"} />
        </NavLink>
        <NavLink to="/help" activeClassName="active" onClick={() => setActiveText("Help")}>
          <SidebarItem icon={<LifeBuoy size={20} />} text="Help" isActive={activeText === "Help"} />
        </NavLink>
      </Sidebar>
      <div className="content w-full">{children}</div>
    </div>
  );
};

export default Layout;
