import React from 'react';

const Audio = () => {
    const [matchingResults, setMatchingResults] = React.useState([]);
    const [noMatch, setNoMatch] = React.useState(false);
    const [isLoading, setIsLoading] = React.useState(false);

    const imageList = [1, 2];

    const handleClick = (i) => {
        setIsLoading(true);
        setMatchingResults([]);
        setNoMatch(false);
        const baseUrl = 'http://127.0.0.1:5000/analyze-audio';
        const url = `${baseUrl}/${i}`;
        fetch(url)
            .then(res => res.json())
            .then(res => {
                if (res.success) {
                    setMatchingResults(res.data[0]);
                } else {
                    setNoMatch(true);
                }
            })
            .finally(() => {
                setIsLoading(false);
            });
    };

    
    const buttonsContainerStyle = {
        display: 'flex',
        gap: '10px',
    };

    const buttonStyle = {
        backgroundColor: '#E1F4F3',
        padding: '10px',
        borderRadius: '5px',
        cursor: 'pointer',
    };

    
    

    const spinnerStyle = {
        fontSize: '24px', // Increase spinner size
    };

    return (
        <>
            <div style={containerStyle}>
                <div className='flex gap-5'>
                    {imageList?.map((item) => (
                        <div className='p-10 background-red' key={item} onClick={() => handleClick(item)} style={buttonStyle}>
                            {item}
                        </div>
                    ))}
                </div>
                <div className='flex justify-center items-center'>
                    {isLoading ? (
                        <div style={spinnerStyle}>Loading....</div>
                    ) : (
                        <>
                            {matchingResults && (
                                <div>
                                    {matchingResults.folder_name}
                                    {matchingResults.matching_percentage}
                                </div>
                            )}
                            {noMatch && <div>No match</div>}
                        </>
                    )}
                </div>
            </div>
        </>
    );
};

export default Audio;
