import React from 'react'

const Audio = () => {

    const [matchingResults, setMatchingResults] = React.useState([])
    const [noMatch, setNoMatch] = u
    const [isLoading, setIsLoading] = React.useState(false)

    const imageList = [1, 2];

    const handleClick = (i) => {
        setIsLoading(true)
        setMatchingResults([])
        const baseUrl = 'http://127.0.0.1:5000/analyze-audio'
        console.log(i)
        const url = `${baseUrl}/${i}`
        fetch(url)
            .then(res => res.json())
            .then(res => {
                if (res.success) {
                    setMatchingResults(res.data[0])
                }
            })
            .finally(() => {
                setIsLoading(false)
            })
    }

    if (isLoading)
        return <div>Loading....</div>

    return (
        <>
            <div className='flex gap-5'>
                {imageList?.map((item) => (
                    <>
                        <div className='p-10 background-red' key={item}
                            onClick={() => handleClick(item)}
                        >{item}</div>
                    </>
                ))}
            </div>
            <div className='flex justify-center items-center'>
                {matchingResults ? (
                    <div>
                        {matchingResults.folder_name}

                    </div>
                ) : (
                    <div>no mathch</div>
                )}
            </div>
        </>
    )
}

export default Audio
